async function processModel(input, config, abortSignal) { 
   // 构建请求数据 
   const requestData = { 
     messages: input.messages, 
     model: 'deepseek-ai/DeepSeek-V3', 
     stream: true, 
     max_tokens: 4096, 
     temperature: 0.7, 
     top_p:0.9 
   }; 

   console.log('SiliconFlow 请求:', requestData); 

   try { 
     // 发送请求到SiliconFlow API 
     const response = await fetch(`${config.baseUrl}`, { 
       method: 'POST', 
       headers: { 
         'Authorization': `Bearer ${config.apiKey}`, 
         'Content-Type': 'application/json' 
       }, 
       body: JSON.stringify(requestData),
       signal: abortSignal
     }); 

     if (!response.ok) { 
       const errorText = await response.text(); 
       throw new Error(`SiliconFlow API 错误 ${response.status}: ${errorText}`); 
     } 

     // 处理流式响应 
     const reader = response.body.getReader(); 
     const decoder = new TextDecoder(); 

     // 返回异步生成器处理流式数据 
     return (async function* () { 
       let buffer = ''; 

       try { 
         while (true) { 
           const { done, value } = await reader.read(); 

           if (done) { 
             break; 
           } 

           // 解码数据块 
           buffer += decoder.decode(value, { stream: true }); 
           const lines = buffer.split('\n'); 
           buffer = lines.pop() || ''; 

           for (const line of lines) { 
             const trimmedLine = line.trim(); 
             if (trimmedLine === '' || trimmedLine === 'data: [DONE]') { 
               continue; 
             } 

             if (trimmedLine.startsWith('data: ')) { 
               try { 
                 const jsonStr = trimmedLine.slice(6); 
                 const data = JSON.parse(jsonStr); 

                 if (data.choices && data.choices[0] && data.choices[0].delta) { 
                   const content = data.choices[0].delta.content || ''; 
                   const finished = data.choices[0].finish_reason === 'stop'; 

                   if (content || finished) { 
                     yield { 
                       content: content, 
                       finished: finished 
                     }; 
                   } 
                 } 
               } catch (parseError) { 
                 console.warn('解析SSE数据失败:', parseError, trimmedLine); 
               } 
             } 
           } 
         } 
       } finally { 
         reader.releaseLock(); 
       } 
     })(); 

   } catch (error) { 
     console.error('SiliconFlow API 调用失败:', error); 
     throw error; 
   } 
 }

